import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { Row, Col, Card, CardHeader, CardBody, FormGroup, Label, Input, Button, InputGroup, InputGroupAddon, CardFooter, Form, FormFeedback } from 'reactstrap';
import { AppSwitch } from '@coreui/react'
import '../../../global.css';
import api from '../../../../src/services/api';

export default function Modulo() {
    const [nomeModulo, setnomeModulo] = useState('');
    const [descricao, setdescricao] = useState('');    
    const [ativo, setAtivo] = useState(1);
    const [usuarioId, setusuarioId] = useState('0000-00-00');
    const [dataultmodif, setDataultmodif] = useState('0000-00-00');
    const history = useHistory();

    async function handleRegister(e) {
        e.preventDefault();
        
        const data = {
          nomeModulo,
          descricao,          
          ativo, 
          usuarioId,
          dataultmodif
        };
    
        try {
            console.log(data);
            const response = await api.post('/usuario', data);
            alert(`Seu login de acesso: ${response.data.login}`);      
            history.push('/');
    
        } catch (err) {
    
            alert('Erro no cadastro, tente novamente.');    
        }
    }
    return (        
        <div className="animated fadeIn">
            <Form onSubmit={handleRegister}>
                <Row>                              
                    <Col xs="12" md="12">
                        <Card>
                            <CardHeader>
                                <strong>Módulo</strong>
                                <small> novo</small>
                            </CardHeader>
                            <CardBody>
                                <FormGroup row>
                                    <Col md="12">
                                        <Label htmlFor="Nome">Nome Módulo</Label>
                                        <Input type="text" required id="txtNomeModulo" placeholder="Digite Nome do Módulo"
                                        />                                        
                                    </Col>
                                </FormGroup> 
                                <FormGroup> 
                                    <Col md="5">
                                        <Label htmlFor="Descricao">Descrição</Label>
                                        <Input type="text" required id="txtDescricao" placeholder="Digite a Descrição do Módulo" />
                                    </Col>
                                    <Col md="2">
                                        <Label htmlFor="DataNasc">Ativo</Label>
                                        <AppSwitch id="rdAtivo" className={'switch-ativo'}  label color={'success'} defaultChecked size={'sm'}/>
                                    </Col>                                                           
                                </FormGroup>                                                                                         
                            </CardBody>
                            <CardFooter className="text-center">
                                <Button type="submit" size="sm" color="success" className=" mr-3"><i className="fa fa-check"></i> Salvar</Button>
                                <Button type="reset" size="sm" color="danger" className="ml-3"><i className="fa fa-ban "></i> Cancelar</Button>
                            </CardFooter>
                        </Card>
                    </Col>
                </Row>
            </Form>
        </div>    
    );    
}
